import React, { Component } from 'react'

export default class Login extends Component {
  constructor(props){
    super(props)
    this.state = {
    uname:'',
    password:'',
    goto: false
    }
    }
     
    componentWillMount(){
    console.log("Component will mount")
    }
     
    getLogin=()=>{
    console.log(`in login`,this.state.email,this.state.password)
    ApiFun.postApi('login',{email:this.state.email,password:this.state.password})
    .then(json => {
    if(json.data.result && json.data.result.length>0){
    this.props.history.push('/home')
    }
    else{
    this.props.history.push('/login')
    }
    }).catch((error)=>{
    alert('login failed. Try later!')
    })
    }
     
    handleChange= (e)=> {
    this.setState({[e.target.name]:e.target.value});
    }
  render() {
    return (
      <form className="container" onSubmit={this.handleSubmit}>
      <div className="CreateBooking">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <br />
            <div className="card">
              <div className="card-header bg-custom">
                <h3>LOGIN</h3>
              </div>
              <div className="card-body">
                { 
                  <div class="form-group">
                  
                  
                  <label className="font-weight-bold">Username:</label>
                   <input type="text" className="form-control" 
                   value={this.state.formData.uname}
                   name="uname" onChange={this.handleChange}
                   ></input>
                  <p className="text-danger">{this.state.formErrorMessage.uname}</p>

                  <label className="font-weight-bold">Password:</label>
                   <input type="password" className="form-control" 
                   value={this.state.formData.password}
                   name="password" onChange={this.handleChange}
                   ></input>
                   <p className="text-danger">{this.state.formErrorMessage.password}</p>
                   </div>  
                }
                <center><button type="submit" style={{width:"500px"}}
                disabled={
                  !(this.state.formValid.uname && this.state.formValid.password)
                } className="btn btn-primary" >Login</button></center>
               <Link to="/register" className="btn btn-link">Register</Link>
               <div align="center">
                  {
                     <p className="text-success">{this.state.successMessage}</p>
                  }
                  </div>
                <div align="center">
                  {
                     <p className="text-danger">{this.state.ErrorMessage}</p>
                  }
                  
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      </form>
    )
  }
}
