import React, { Component } from 'react'
import axios from 'axios';
const url = "http://localhost:4000/movieList/";
export default class UpdateMovie extends Component {
    constructor(props){
        super(props);
            this.state={
                moviesData: "",
            }
        };
    updatedata=(id)=>{
        const data={
                    movieId: "M1001",
                    movieName:"Kabali",
                    cost:"500",
                    path:"-",
        };
        axios.put(url, data)
        .then((data)=>{
            this.setState({
              successMessage: "Booking created successfully!!",
              errorMessage: "",
            });
        })
        .catch((error) => {
            console.log(error);
    })
};
componentDidMount(){
    this.updatedata();
}
  render() {
    return (
        <div>
        <button onClick={this.updatedata()} >Update</button>
        <p>{this.state.successMessage}</p>
        <p>{this.state.errorMessage}</p>
        </div>
    )
  }
}
