import React, { Component } from 'react'
import axios from "axios";
import { Link } from 'react-router-dom';
import "bootstrap/dist/css/bootstrap.min.css";
const url2="http://localhost:4000/login/";
export default class Login extends Component {
  constructor(props){
    super(props)
    this.state = {
    uname:'',
    password:'',
    goto: false,
    errorMessage: "",
    successMessage: "",
    register:[],
    }
    
    }
     
    componentWillMount(){
    console.log("Component will mount")
    }
     
    getLogin=()=>{
    console.log(`in login`,this.state.uname,this.state.password)
    var username = this.state.uname;
    var passwordd = this.state.password;
    axios.post(url2,{uname:this.state.uname,password:this.state.password}, 
      {
      register: {
        username: this.state.uname,
        passwordd: this.state.password
      }})
      .then(()=>{
        if(this.state.uname===username && this.state.password===passwordd){
            console.log('Authenthicate');
        } 
        else{
          console.log(this.state.uname);
          console.log(username);
          console.log('Not Authenthicate');
        }
      }).catch(function(error) {
        console.log('Error on Authentication');
      });
    }
     
    handleChange= (e)=> {
    this.setState({[e.target.name]:e.target.value});
    }
  render() {
    return (
      <form className="container">
      <div className="CreateBooking">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <br />
            <div className="card">
              <div className="card-header bg-custom">
                <h3>LOGIN</h3>
              </div>
              <div className="card-body">
                { 
                  <div class="form-group">
                  
                  
                  <label className="font-weight-bold">Username:</label>
                   <input type="text" className="form-control" 
                   value={this.state.uname}
                   name="uname" onChange={this.handleChange}
                   ></input>
                  

                  <label className="font-weight-bold">Password:</label>
                   <input type="password" className="form-control" 
                   value={this.state.password}
                   name="password" onChange={this.handleChange}
                   ></input>
                   
                   </div>  
                }
                <center><button type="button" className="btn btn-primary" onClick={this.getLogin}
                >Login</button></center>
               <Link to="/register" className="btn btn-link">Register</Link>
               <div align="center">
                    {
                       <p className="text-success">{this.state.successMessage}</p>
                    }
                    </div>
                  <div align="center">
                    {
                       <p className="text-danger">{this.state.errorMessage}</p>
                    }
                    
                  </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      </form>
    )
  }
}
