import React, { Component } from 'react'
import axios from 'axios';
const url = "http://localhost:4000/movieList/";

export default class UpdateMovie extends Component {
    constructor(props) {
        super(props);
        this.state = {
          form: {
            movieId: "",
            movieName:"",
            cost:"",
            path:"",
          },
          formErrorMessage: {
            movieId: "",
            movieName:"",
            cost:"",
            path:"",
          },
          formValid: {
            movieId: false,
            movieName: false,
            cost:false,
            path:false,
          },
          movieList: [],
          errorMessage: "",
          successMessage: "",
        };
      }
      submitBooking = (event) => {
        const newForm = {
            ...this.state.form,
          };
        axios
        .post(url,newForm)
        .then((res)=>{
            this.setState({
              successMessage: "Booking created successfully!!",
              errorMessage: "",
            });
        })
        .catch((error) => {
          if (error.response == undefined) {
            this.setState({
              errorMessage: "Please start your JSON Server",
              successMessage: "",
            });
          } else if (error.response.status == 404) {
            this.setState({
              errorMessage: "Booking Failed!",
              successMessage: "",
            });
          }
        });
    };
  render() {
    const { updatedAt } = this.state;
    return (
        <div className="card text-center m-3">
                <h5 className="card-header">Simple PUT Request</h5>
                <div className="card-body">
                    Returned Update Date: {updatedAt}
                </div>
            </div>
    )
  }
}
