import React, { Component } from 'react'
import axios from 'axios';
const url = "http://localhost:4000/movieList/";

export default class UpdateMovie extends Component {
   constructor(props){
    super(props);
    this.state={
            id:"",
            movieId:"",
            movieName:"",
            cost:"",
            path:"",
        movies:[],
   };
}
fetchMovies = () => {
    axios.get(url)
    .then((res)=>{
        this.setState({
            movies:res.data,
        });
    })
    .catch((err)=>{
        console.log("Error :",err);
    });
};
componentDidMount() {
    this.fetchMovies();
  }
      UpdateBooking = () => {
            console.log(this.state.movieId);
            console.log(this.state.movieName);
            axios.put("http://localhost:4000/movieList/",{
            
                movieId:this.state.movieId,
                movieName:this.state.movieName
            })
            .then((response)=>{
                console.log(response);
            })
            .catch((e)=>{
                console.log(e);
            })
        };
   
    
        handleChange = (event) => {
               this.setState({[event.target.name]:event.target.value});
       };
     
  render() {
    return (
       <form onSubmit={this.UpdateBooking}>
        <div className="CreateBooking">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <br />
            <div className="card">
              <div className="card-header bg-custom">
                <h3>UPDATE MOVIES</h3>
              </div>
              <div className="card-body">
                { 
                  <div class="form-group">
                  
                  <label className="font-weight-bold">Movie ID:</label>
                  <input type="text" className="form-control" 
                  value={this.state.movieId}
                  name="movieId" placeholder="e.g.- M1001" onChange={this.handleChange}></input>
                 
                 
                   <label className="font-weight-bold">Movie Name:</label>
                   <input type="text" className="form-control" 
                   value={this.state.movieName}
                   name="movieName" 
                   onChange={this.handleChange}></input>
                  
                  
                  <label className="font-weight-bold">Movie Cost:</label>
                   <input type="number" className="form-control"
                   value={this.state.cost}
                   name="cost" 
                   onChange={this.handleChange}></input>
                 

                  <label className="font-weight-bold">Movie Image Address:</label>
                   <input type="text" className="form-control" 
                   value={this.state.path}
                   name="path" 
                   onChange={this.handleChange}></input>
                  
                  <br/>
                   </div>  
                }
                <button type="submit" 
                className="btn btn-primary">Update Movie</button>
                
                  
               
              </div>
            </div>
          </div>
        </div>
      </div>
      </form>
    )
  }
}
