import React, { Component } from 'react'
import "bootstrap/dist/css/bootstrap.min.css";
import axios from 'axios';
const url1 = "http://localhost:4000/movieList/";
export default class ViewBooking extends Component {
    state={
        movies:[],
        errormessage:""
    };
    fetchMovies = () => {
        axios.get(url1)
        .then((res)=>{
            this.setState({
                movies:res.data,
            });
        })
        .catch((err)=>{
            console.log("Error :",err);
        });
    };
    componentDidMount() {
        this.fetchMovies();
      }
  render() {
    return (
        <div className="container p-3">
          <div className="row">
            {/* Display all the fligts in the below card format */}
            {this.state.movies.map((movie) => (
              <div className="col-md-4 col-sm-6 mb-1">
                <div className="card col">
                  <div className="card-header">
                    <h5 className='card-title'>{movie.movieName}</h5></div>
                  <div className="card-body">
                    <img src={movie.path} style={{"height" : "300px", "width" : "300px"}}/>
                    <h5 className="card-title">
                      Movie Id :{movie.movieId}
                    </h5>
                    <p className="card-text">Fare : Rs. {movie.cost}</p>
                   <button>
                      Book
                    </button>
                    {/**/}
                  </div>
                </div>
              </div>
            ))}
          </div>
        <div align="center">
           {/* Display rror message as given in QP */}
        </div>
      </div>
    )
  }
}
