import logo from './logo.svg';
import './App.css';
import { Component } from "react";
class App extends Component{
constructor(props)
{
super(props)
this.state={
  name:"",
  age:null,
  errmsg:""
};
}
funChange=(event)=>{
  let n=event.target.name;
  let v=event.target.value;
  let err;
  if(n==="age")
  {
    if(v>=18)
    {
      err=<strong>Age is below 18</strong>
    }
  }
  this.setState({errmsg:err})
  this.setState({[n]:v})
}
funSubmit=(event)=>{
  event.preventDefault();
  alert(this.state.name+" Form Created Successfully ")
}
  render(){
    return(
      <>
        <form onSubmit={this.funSubmit}>
        <div>
          <label>Enter a Name:</label>
          <input type="text" name="name" onChange={this.funChange} />
          <label>Enter a Age:</label>
          <input type="text" name="age" onChange={this.funChange} />
          <button type="submit">Submit</button>

        </div>
        </form>
      </>
    );
  }
}
export default App;
