import React, { Component } from 'react'
import axios from 'axios';
const url = "http://localhost:4000/movieList/";

export default class UpdateMovie extends Component {
   state={
        updated:{
            id:"",
            movieId:"",
            movieName:"",
            cost:"",
            path:"",
        },
   };
      UpdateBooking = (event) => {
            console.log(this.state.updated.movieId);
            console.log(this.state.updated.movieName);
            axios.put(url+this.state.updated.id)
            .then((response)=>{
                console.log(response);
            })
            .catch((e)=>{
                console.log(e);
            })
        };
   
    
      handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        const newState={
            updated: {
              ...this.state.form,
              [name]: value,
            },
          };
          this.setState(newState);
      };
     
  render() {
    return (
       
        <div className="CreateBooking">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <br />
            <div className="card">
              <div className="card-header bg-custom">
                <h3>UPDATE MOVIES</h3>
              </div>
              <div className="card-body">
                { 
                  <div class="form-group">
                  
                  <label className="font-weight-bold">Movie ID:</label>
                  <input type="text" className="form-control" 
                 
                  name="movieId" placeholder="e.g.- M1001" onChange={this.handleChange}></input>
                 
                 
                   <label className="font-weight-bold">Movie Name:</label>
                   <input type="text" className="form-control" 
                   name="movieName" 
                   onChange={this.handleChange}></input>
                  
                  
                  <label className="font-weight-bold">Movie Cost:</label>
                   <input type="number" className="form-control"
                   name="cost" 
                   onChange={this.handleChange}></input>
                 

                  <label className="font-weight-bold">Movie Image Address:</label>
                   <input type="text" className="form-control" 
                   name="path" 
                   onChange={this.handleChange}></input>
                  
                  <br/>
                   </div>  
                }
                <button type="submit" 
                className="btn btn-primary" onClick={this.UpdateBooking}>Update Movie</button>
                
                  
               
              </div>
            </div>
          </div>
        </div>
      </div>
   
    )
  }
}
