import React, { Component } from 'react'
import axios from "axios";
import { Link } from 'react-router-dom';
import "bootstrap/dist/css/bootstrap.min.css";
import { authenticate } from "../services/login.service";
export default class Login extends Component {
  state={
    formdata:{
        email:"",
        password: "",
    },
    formError:{
        emailErr: "",
        passErr: "",
    },
    
    successMessage: "",
    errMessage: "",
    showPassword: false,
    
}
validateField = (name, value) => {
  if (name === "emailId") {
    let emailRegx =
      /^([a-z][a-zA-Z0-9_]*(\.[a-zA-Z][a-zA-Z0-9_]*)?@[a-z][a-zA-Z-0-9]*\.[a-z]+(\.[a-z]+)?)|[7-9][0-9]{9}$/;
    if (!value.match(emailRegx)) {
      this.setState({
          formError:{
            ...this.state.formError,
              emailErr:'Enter a valid Email, Eg:"abc@gmail.com"'
          }
      })
    } else {
      this.setState({
          formError:{
            ...this.state.formError,
              emailErr:''
          }
      })          }
  } else if (name === "password") {
    let passRegx = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*\W).{7,20}$/;
    if (!value.match(passRegx)) {
      this.setState({
          formError:{
            ...this.state.formError,
              passErr:"Password should contain atleast one speacial char,numbers,capital and small letter!"
          }
      })
    } else {
      this.setState({
          formError:{
            ...this.state.formError,
              passErr:""
          }
      })
    }
  }
};
handleChange = (e) => {
  const name = e.target.name;
  const value = e.target.value;
  this.setState({
      formdata: {
        ...this.state.formdata,
          [name]: value
      }
  })
  this.validateField(name, value);
};
formSubmit = async (formObj) => {
  try {
    const isAuthenticated = await authenticate(
      formObj.emailId,
      formObj.password
    );
    if (isAuthenticated) {

      this.setState({
          successMessage:"Login Successfull",
         
          errMessage:''
      });
      
      setTimeout(()=>this.props.history.push("/addmovies"), 1500);

      
    } else {
      this.setState({
          errMessage:"Invalid username or password!",
        successMessage:""
      }
      );
    }
  } catch (error) {
      this.setState({
          errMessage:error.message,
          successMessage:""
      }
      );
  }
};

handleSubmit = (e) => {
  e.preventDefault();
  let formObj={
      emailId:this.state.formdata.emailId,
      password:this.state.formdata.password
  }
  this.formSubmit(formObj);
};
     
     
