      <div className="CreateBooking">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <br />
            <div className="card">
              <div className="card-header bg-custom">
                <h3>LOGIN</h3>
              </div>
              <div className="card-body">
                { 
                  <div class="form-group">
                  
                  
                  <label className="font-weight-bold">Email Id:</label>
                   <input type="email" className="form-control" 
                    value={this.state.formdata.emailId}
                    name="emailId" onChange={this.handleChange}
                   ></input>
                  

                  <label className="font-weight-bold">Password:</label>
                   <input type="password" className="form-control" 
                   value={this.state.formdata.password}
                   name="password"
                 onChange={this.handleChange}
                   ></input>
                   
                   </div>  
                }
                <center><button type="submit" className="btn btn-primary"  
                disabled={!(this.state.formError.emailErr === "" && this.state.formError.passErr === "")}
                >Login</button></center>
               <Link to="/register" className="btn btn-link">Register</Link>
               <div align="center">
                    {
                       <p className="text-success">{this.state.successMessage}</p>
                    }
                    </div>
                  <div align="center">
                    {
                       <p className="text-danger">{this.state.errMessage}</p>
                    }
                    
                  </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      </form>
    )
  }
