import React, { Component } from 'react'
import axios from 'axios';
const url = "http://localhost:4000/movieList/";
export default class UpdateMovie extends Component {
    constructor(props){
        super(props);
            this.state={
                form: {
                    movieId: "M1001",
                    movieName:"Kabali",
                    cost:"500",
                    path:"-",
                  },
            }
        };
    updatedata=()=>{
        const data={
            ...this.state.form,
        };
        axios.put(url,data)
        .then((data)=>{
            this.setState({
              successMessage: "Booking created successfully!!",
              errorMessage: "",
            });
        })
        .catch((error) => {
         
            this.setState({
              errorMessage: "Please start your JSON Server",
              successMessage: "",
            });
    
    })
};
  render() {
    return (
     
        <button onClick={this.updatedata} >Update</button>
     
    )
  }
}
